#include "Asynchronous.h"Define_Module(Asynchronous);	void Asynchronous::initialize() {		this->isAConsumerHungry = false;	}	void Asynchronous::handleMessage(cMessage *msg) {		switch(msg->getKind()) {		case GET:			if((this->queue).empty()) {				this->isAConsumerHungry = true;			} else {				int token = queue.front();				this->queue.pop_front();				Data *msg = new Data("Data", DATA);				msg->token = token;				send(msg, outReader);			}			break;		case PUT:			Ack *msg = new Ack("Ack", ACK);			send(msg, outWriter);			if(this->isAConsumerHungry) {				Data *msgData = new Data("Data", DATA);				send(msgData, outReader);				this->isAConsumerHungry = false;			} else {				this->queue.push_back(1);			}			break;		default:			break;		}	}