#include "Synchronous.h"Define_Module(Synchronous);	void Synchronous::initialize() {		this->isAConsumerHungry = false;		this->isAProducerHungry = false;		this->token = NULL;	}	void Synchronous::handleMessage(cMessage *msg) {		switch(msg->getKind()) {		case GET:			this->isAConsumerHungry = true;			if (this->isAProducerHungry) {				this->executeRdv();			}			break;		case PUT:			this->isAProducerHungry = true;			this->token = 1;			if (this->isAConsumerHungry) {				this->executeRdv();			}			break;		default:			break;		}	}		void Synchronous::executeRdv() {		Ack *ackMsg = new Ack("Ack", ACK);		send(ackMsg, outWriter);		Data *dataMsg = new Data("Data", DATA);		send(dataMsg, outReader);		this->isAProducerHungry = false;		this->isAConsumerHungry = false;		this->token = NULL;	}